dplyr::inner_join(geo, cuisine, by="PLACEID") %>% select(CUISINE, PRICE) %>% filter(CUISINE %in% c("American", "Greek", "Dutch-Belgian", "French", "Japanese", "Mexican", "Vegetarian", "Chinese", "Cafe_Coffee_Shop", "Bar_Pub_Brewery") ) %>% ggplot(aes(x=PRICE, y=CUISINE, color = PRICE)) + scale_x_discrete(limits=c("low", "medium", "high")) +geom_point() + geom_jitter(alpha=1, width=.05)
