dplyr::inner_join(geo, cuisine, by="PLACEID") %>% select(RCUISINE, PRICE, STATES) %>% filter (STATES %in% c("Mexico", "Morelos", "San Luis Potosi", "Tamaulipas"))%>%  filter(RCUISINE %in% c("American", "Greek", "Dutch-Belgian", "French", "Japanese", "Mexican", "Vegetarian", "Chinese", "Cafe_Coffee_Shop", "Bar_Pub_Brewery") ) %>% ggplot(aes(x=RCUISINE, y=PRICE, color = STATES)) + scale_y_discrete(limits=c("low", "medium", "high")) +geom_point() + geom_jitter(alpha=1, width=.05)
