
dplyr::full_join( geo, (right_join(parking, cuisine, by="PLACEID")), by= "PLACEID") %>% select(CUISINE, PARKING, STATES) %>% filter(CUISINE %in% c("American",  "Dutch-Belgian", "French", "Mexican", "Chinese", "Cafe_Coffee_Shop", "Bar_Pub_Brewery") ) %>% filter( PARKING %in% c("fee'", "none", "public", "street", "valet parking", "validated parking")) %>% ggplot(aes(x=PARKING, y=CUISINE, color = STATES)) + scale_x_discrete(limits=c("none", "street", "public", "validated parking", "valet parking")) + geom_point() + geom_jitter(alpha=1, width=.05)
